import echarts from 'echarts';
import '../../../../node_modules/echarts/theme/roma';
export default {
    name: "PersonTimePie",
    data() {
        return {
            chart: null
        };
    },
    methods: {
        drawPie (id) {
            this.chart = echarts.init(document.getElementById(id), 'roma.js');
            this.chart.setOption({
                tooltip: {
                    trigger: 'item',
                    formatter: "{a} <br/>{b} : {c} ({d}%)"
                },
                toolbox: {
                    feature: {
                        saveAsImage: {},
                        dataView: {}
                    },
                    right: 15,
                    top: 10
                },
                legend: {
                    orient: 'vertical',
                    left: 5,
                    top: 10,
                    data: ["国内读研", "工作", "出国深造", "创业"]
                },
                series: [
                    {
                        name: '人数',
                        type: 'pie',
                        selectedMode: 'single',
                        radius: '70%',
                        center: ['50%', '60%'],
                        data: [
                            { "value": 58, "name": "国内读研", "selected": true },
                            { "value": 26, "name": "工作" },
                            { "value": 10, "name": "出国深造" },
                            { "value": 1, "name": "创业" }
                        ],
                        itemStyle: {
                            normal: {
                                borderWidth: 0.5,
                                borderColor: '#ffffff'
                            },
                            emphasis: {
                                shadowBlur: 10,
                                shadowOffset: 0,
                                shadowColor: 'rgba(0, 0, 0, 0.5)'
                            }
                        }
                    }
                ]
            });
        }
    },
    mounted() {
        this.$nextTick(function() {
            this.drawPie('PersonDayPie');
            var that = this;
            var resizeTimer = null;
            window.onresize = function() {
                if (resizeTimer) clearTimeout(resizeTimer);
                resizeTimer = setTimeout(function() {
                    that.drawPie('PersonDayPie');
                }, 100);
            }
        });
    }
}
