import echarts from 'echarts'
export default {
    data() {
        return {
            year:'2020',
            month:'1',
            value:'测试1',
            years:[],
            months:[],
            options:[],
            chooseDate:'',
            address:'',
            classPrefix: "qst-tecs-src-teacher-dean-brokenLine_",
            xNum:[],
            yNum:['当日最高', '当日平均'],
            series:[],   // 折线图数据
        }
    },
    methods: {
        // 图表初始化数据
        initChart(){
            let myChart = echarts.init(this.$refs.myEchart);
            myChart.setOption({
                tooltip: {
                    trigger: 'axis'
                },
                legend: {
                    data: this.yNum,
                },
                grid: {
                    left: '3%',
                    right: '6%',
                    bottom: '3%',
                    top:'12%',
                    containLabel: true
                },
                xAxis: {
                    type: 'category',
                    boundaryGap: false,
                    data: this.xNum,
                    axisLabel: {
                        interval:0,
                        rotate:-60
                    }
                },
                yAxis: {
                    type: 'value',
                    min: 0,
                    max: 100,
                    interval: 20,
                    axisLabel: {
                        formatter: '{value}'
                    }
                },
                series: this.series,
            })
        },
        getSeries(){
            this.getXNum();
            const that = this
            this.$axios.post('http://localhost:9099/tourist/count/monthCount',{
                year:this.year,
                month:this.month,
                address:this.address
            }).then( successResponse => {
                that.series = successResponse.data
                this.initChart();
            })
        },
        getXNum(){
            const that = this
            this.$axios.post('http://localhost:9099/tourist/count/getMonthDate',{
                year:this.year,
                month:this.month,
                address:this.address
            }).then(successResponse => {
                that.xNum = successResponse.data
            })
        },
        getAddressName(){
            const that = this
            this.$axios.get('http://localhost:9099/tourist/count/allAddressName').then(successResponse => {
                that.options = successResponse.data
            })
        },
        getYear(){
            const that = this
            this.$axios.get("http://localhost:9099/tourist/count/year").then(successResponse => {
                that.years = successResponse.data
            })
        },
        getMonth(year){
            const that = this
            this.$axios.get("http://localhost:9099/tourist/count/month?year="+year).then(successResponse => {
                that.months = successResponse.data
            })
        },
        currentSel(selVal) {
            this.address = selVal
        },
        currentYearSel(selVal) {
            this.year = selVal
            this.getMonth(this.year)
        },
        currentMonthSel(selVal) {
            this.month = selVal
        }
    },
    created() {
        this.getYear();
        this.getMonth(this.year);
        this.getXNum();
        this.currentSel(this.value);
        this.getAddressName();
        this.getSeries();
    },
    mounted () {
        let obj = this.$refs.myEchart;
        if(obj){
            this.initChart();
        }
    }
}
