import echarts from 'echarts'
export default {
    data() {
        return {
            value:'测试1',
            options:[],
            param: {
                date:this.chooseDate,
                address:''
            },
            classPrefix: "qst-tecs-src-teacher-dean-brokenLine_",
            xNum:['09:00-10:00', '10:00-11:00', '11:00-12:00','12:00-13:00', '13:00-14:00', '14:00-15:00', '15:00-16:00', '16:00-17:00'],
            yNum:['小时平均', '小时最高'],
            series:[],   // 折线图数据
        }
    },
    methods: {
        // 图表初始化数据
        initChart(){
            let myChart = echarts.init(this.$refs.myEchart);
            myChart.setOption({
                tooltip: {
                    trigger: 'axis'
                },
                legend: {
                    data: this.yNum,
                },
                grid: {
                    left: '3%',
                    right: '6%',
                    bottom: '3%',
                    top:'12%',
                    containLabel: true
                },
                xAxis: {
                    type: 'category',
                    boundaryGap: false,
                    data: this.xNum,
                    axisLabel: {
                        interval:0,
                        rotate:-60
                    }
                },
                yAxis: {
                    type: 'value',
                    min: 0,
                    max: 100,
                    interval: 20,
                    axisLabel: {
                        formatter: '{value}'
                    }
                },
                series: this.series,
            })
        },
        getSeries(){
            const that = this
            this.$axios.post('http://localhost:9099/tourist/count/todayCount',{
                date:this.param.chooseDate,
                address:this.param.address
            }).then( successResponse => {
                console.log(this.param.chooseDate)
                console.log(this.param.address)
                that.series = successResponse.data
                this.initChart();
            })
        },
        getAddressName(){
            const that = this
            this.$axios.get('http://localhost:9099/tourist/count/allAddressName').then(successResponse => {
                that.options = successResponse.data
            })
        },
        currentSel(selVal) {
            this.param.address = selVal
         }
    },
    created() {
        this.currentSel(this.value);
        this.getAddressName();
        this.getSeries();
    },
    mounted () {
        let obj = this.$refs.myEchart;
        if(obj){
            this.initChart();
        }
    }
}
