import echarts from 'echarts'
export default {
    name: '',
    data() {
        return {
            charts: '',
            opinionData: ["3", "2", "4", "4", "5","0"]
        }
    },
    methods: {
        drawLine(id) {
            this.charts = echarts.init(document.getElementById(id))
            this.charts.setOption({
                tooltip: {
                    trigger: 'axis'
                },
                legend: {
                    data: ['近七日收益']
                },
                grid: {
                    left: '3%',
                    right: '4%',
                    bottom: '3%',
                    containLabel: true
                },

                toolbox: {
                    feature: {
                        saveAsImage: {}
                    }
                },
                xAxis: {
                    type: 'category',
                    boundaryGap: false,
                    data: ["9:00-10:00","10:00-11:00","11:00-12:00","12:00-13:00","13:00-14:00","14:00-15:00","15:00-16:00","16:00-17:00"]

                },
                yAxis: {
                    type: 'value'
                },

                series: [{
                    name: '平均人数',
                    type: 'line',
                    stack: '总量',
                    data: this.opinionData
                }]
            })
        }
    },
    //调用
    mounted() {
        this.$nextTick(function() {
            this.drawLine('main')
        })
    }
}
